mapper.update:
update approval 
set apr_status = #{status}
where apr_id = #{apr_id}

merge into temp_apr
using dual on(apr_id = #{apr_id} and emp_id = #{emp_id})
when matched then update set af_id = #{af_id}, apr_title = #{apr_title}, apr_content = #{apr_content}
when not matched then
	insert (temp_id, apr_id, dept_id, emp_id, af_id, apr_title, apr_content)
	values (temp_apr_seq.nextval, #{apr_id}, #{dept_id}, #{emp_id}, #{af_id}, #{apr_title}, #{apr_content})
